mapper.select:
select rb.recruit_board_id, rb.title, m.nickname, rb.views, count(case when rm.state = 1 then 1 end) as current, rb.recruit_total, rb.state, rb.created_date from recruit_board rb inner join member m on rb.member_id = m.member_id left join recruit_member rm on rb.recruit_board_id = rm.recruit_board_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}
and rb.theme_id=#{themeId}
group by rb.recruit_board_id order by rb.recruit_board_id desc limit #{rowCount} offset #{offset}

select count(*) from recruit_board rb inner join member m on rb.member_id = m.member_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}
and rb.theme_id=#{themeId}
