mapper.select:
select rb.review_board_id, rb.title, rb.created_date, rb.views, rb.content, c, m.member_id, m.nickname, t.theme_name, r.region_id, r.region_name, rp.review_photo_id, rp.photo from review_board rb inner join member m on m.member_id = rb.member_id inner join theme t on rb.theme_id = t.theme_id inner join region r on r.region_id=rb.region_id left outer join review_photo rp on rp.review_board_id = rb.review_board_id left outer join (select review_board_id, count(member_id) c from review_like group by review_board_id) as rl on rl.review_board_id=rb.review_board_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}
and rb.theme_id=#{themeId}
group by rb.review_board_id order by rb.review_board_id desc limit #{rowCount} offset #{offset}

select count(*) as c from review_board as rb inner join member m on rb.member_id = m.member_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}

select rb.review_board_id, rb.title, rb.created_date, rb.views, m.nickname, t.theme_name, r.region_name, rp.photo from review_board rb inner join member m on m.member_id = rb.member_id inner join theme t on rb.theme_id = t.theme_id inner join region r on r.region_id = rb.region_id left outer join review_photo rp on rp.review_board_id = rb.review_board_id left outer join (select review_board_id, count(member_id) c from review_like group by review_board_id) as rl on rl.review_board_id = rb.review_board_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}
and rb.theme_id=#{themeId}
group by review_board_id order by created_date desc limit #{rowCount} offset #{offset}

select rb.review_board_id, rb.title, rb.created_date, rb.views, c, m.nickname, t.theme_name, r.region_name, rp.photo from review_board rb inner join member m on m.member_id = rb.member_id inner join theme t on rb.theme_id = t.theme_id inner join region r on r.region_id=rb.region_id left outer join review_photo rp on rp.review_board_id = rb.review_board_id left outer join (select review_board_id, count(member_id) c from review_like group by review_board_id) as rl on rl.review_board_id=rb.review_board_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}
and rb.theme_id=#{themeId}
group by rb.review_board_id order by c desc limit #{rowCount} offset #{offset}

select rb.review_board_id, rb.title, rb.created_date, rb.views, c, m.nickname, t.theme_name, r.region_name, rp.photo from review_board rb inner join member m on m.member_id = rb.member_id inner join theme t on rb.theme_id = t.theme_id inner join region r on r.region_id=rb.region_id left outer join review_photo rp on rp.review_board_id = rb.review_board_id left outer join (select review_board_id, count(member_id) c from review_like group by review_board_id) as rl on rl.review_board_id=rb.review_board_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}
and rb.theme_id=#{themeId}
group by rb.review_board_id order by views desc limit 5

select rb.review_board_id, rb.title, rb.created_date, rb.views, c, m.nickname, t.theme_name, r.region_name, rp.photo from review_board rb inner join member m on m.member_id = rb.member_id inner join theme t on rb.theme_id = t.theme_id inner join region r on r.region_id=rb.region_id left outer join review_photo rp on rp.review_board_id = rb.review_board_id left outer join (select review_board_id, count(member_id) c from review_like group by review_board_id) as rl on rl.review_board_id=rb.review_board_id where 1 = 1
and ${filter} like concat('%',#{keyword},'%')
and rb.region_id=#{regionId}
and rb.theme_id=#{themeId}
group by rb.review_board_id order by views desc limit #{rowCount} offset #{offset}
